import{_ as a,c as t,o,aq as r}from"./chunks/framework.CWS5zd3J.js";const h=JSON.parse('{"title":"MKXP","description":"","frontmatter":{"title":"MKXP"},"headers":[],"relativePath":"all-platforms/mkxp.md","filePath":"all-platforms/mkxp.md","lastUpdated":1747548401000}'),n={name:"all-platforms/mkxp.md"};function s(i,e,l,p,u,m){return o(),t("div",null,e[0]||(e[0]=[r('<h1 id="mkxp" tabindex="-1">MKXP <a class="header-anchor" href="#mkxp" aria-label="Permalink to &quot;MKXP&quot;">​</a></h1><p><a href="https://github.com/Ancurio/mkxp" target="_blank" rel="noreferrer">mkxp</a> is a project that seeks to provide a fully open source implementation of the Ruby Game Scripting System (RGSS) interface used in the popular game creation software &quot;RPG Maker XP&quot;, &quot;RPG Maker VX&quot; and &quot;RPG Maker VX Ace&quot; (trademark by Enterbrain, Inc.), with focus on Linux. The goal is to be able to run games created with the above software natively without changing a single file.</p><p>Basically, it can be seen as an <a href="https://github.com/EasyRPG/Player" target="_blank" rel="noreferrer">EasyRPG Player</a> equivalent for &quot;RPG Maker XP&quot;, &quot;RPG Maker VX&quot; and &quot;RPG Maker VX Ace&quot;.</p><h2 id="usage" tabindex="-1">Usage <a class="header-anchor" href="#usage" aria-label="Permalink to &quot;Usage&quot;">​</a></h2><p>Run the executable from the file manager, a launcher or from a terminal emulator with <code>mkxp --gameFolder=&quot;game&quot;</code>. You can change the settings using the <code>--&lt;option&gt;=&lt;value&gt;</code> syntax and by checking the options list inside <a href="https://github.com/Ancurio/mkxp/blob/master/mkxp.conf.sample" target="_blank" rel="noreferrer">mkxp.conf.sample</a>.</p><h3 id="midi-support" tabindex="-1">MIDI support <a class="header-anchor" href="#midi-support" aria-label="Permalink to &quot;MIDI support&quot;">​</a></h3><p>mkxp also supports MIDI playback. Download a sound font and start mkxp with <code>mkxp --gameFolder=&quot;game&quot; --midi.soundFont=/path/to/soundfont.sf2</code>.</p><h2 id="fork-s" tabindex="-1">Fork(s) <a class="header-anchor" href="#fork-s" aria-label="Permalink to &quot;Fork(s)&quot;">​</a></h2><p><a href="https://github.com/mkxp-z/mkxp-z" target="_blank" rel="noreferrer">mkxp-z</a> is a fork of mkxp with more features.</p>',9)]))}const d=a(n,[["render",s]]);export{h as __pageData,d as default};
