import{_ as r,c as t,o as a,aq as o}from"./chunks/framework.CWS5zd3J.js";const d=JSON.parse(`{"title":"ONScripter-EN","description":"","frontmatter":{"0":"t","1":"i","2":"t","3":"l","4":"e","5":" ","6":"=","7":" ","8":"'","9":"O","10":"N","11":"S","12":"c","13":"r","14":"i","15":"p","16":"t","17":"e","18":"r","19":"-","20":"E","21":"N","22":"'"},"headers":[],"relativePath":"all-platforms/onscripter-en.md","filePath":"all-platforms/onscripter-en.md","lastUpdated":1747548877000}`),i={name:"all-platforms/onscripter-en.md"};function n(s,e,l,c,p,h){return a(),t("div",null,e[0]||(e[0]=[o('<h1 id="onscripter-en" tabindex="-1">ONScripter-EN <a class="header-anchor" href="#onscripter-en" aria-label="Permalink to &quot;ONScripter-EN&quot;">​</a></h1><p><a href="https://github.com/Galladite27/ONScripter-EN" target="_blank" rel="noreferrer">ONScripter-EN</a> is fork of <a href="https://ogapee.github.io/www/onscripter_en.html" target="_blank" rel="noreferrer">ONScripter</a> which is an open-source implementation of the NScripter visual novel engine. ONScripter evolved a lot over the years and it spawned different forks like <a href="https://github.com/sekaiproject/ponscripter-fork" target="_blank" rel="noreferrer">Sekai Project&#39;s Ponscripter</a>, <a href="https://github.com/07th-mod/ponscripter-fork" target="_blank" rel="noreferrer">07th-mod&#39;s Ponscripter</a> or <a href="https://github.com/insani-org/onscripter-insani" target="_blank" rel="noreferrer">onscripter-insani</a>. If you want to know more about them, check out <a href="https://kaisernet.org/onscripter" target="_blank" rel="noreferrer">chaoskaiser72&#39;s website</a>.</p><p>Since some of these forks are still maintained, they can improve Windows, macOS and GNU/Linux support for games using old versions of ONScripter. Even if this page gave an overview of the existing forks, we will focus on ONScripter-EN.</p><h2 id="usage" tabindex="-1">Usage <a class="header-anchor" href="#usage" aria-label="Permalink to &quot;Usage&quot;">​</a></h2><p>Follow the instructions from the &quot;Updating ONScripter-EN Games&quot; section on <a href="https://kaisernet.org/onscripter" target="_blank" rel="noreferrer">chaoskaiser72&#39;s website</a>.</p><h2 id="documentation" tabindex="-1">Documentation <a class="header-anchor" href="#documentation" aria-label="Permalink to &quot;Documentation&quot;">​</a></h2><p>If you want to know how to write ONScripter-derived scripts, check out these links:</p><ul><li><a href="https://galladite.net/~galladite/nscripter/guide/guidepages/index.html" target="_blank" rel="noreferrer">galladite&#39;s ONScripter-EN Guide Pages</a></li><li><a href="https://kaisernet.org/onscripter/api/NScrAPI-framed.html" target="_blank" rel="noreferrer">Nscripter API reference</a> (hosted by chaoskaiser72)</li><li><a href="https://sekaiproject.github.io/ponscripter-fork/doc/" target="_blank" rel="noreferrer">Sekai Project&#39;s Ponscripter documentation</a></li><li><a href="https://07th-mod.github.io/ponscripter-fork" target="_blank" rel="noreferrer">07th-mod&#39;s introduction to Ponscripter</a></li></ul>',8)]))}const u=r(i,[["render",n]]);export{d as __pageData,u as default};
