import{_ as a,c as t,o as i,aq as o}from"./chunks/framework.CkWLY5K4.js";const m=JSON.parse(`{"title":"Ayakashibito","description":"","frontmatter":{"0":"t","1":"i","2":"t","3":"l","4":"e","5":" ","6":"=","7":" ","8":"'","9":"A","10":"y","11":"a","12":"k","13":"a","14":"s","15":"h","16":"i","17":"b","18":"i","19":"t","20":"o","21":"'"},"headers":[],"relativePath":"visual-novels/ayakashibito.md","filePath":"visual-novels/ayakashibito.md","lastUpdated":1747549240000}`),l={name:"visual-novels/ayakashibito.md"};function r(n,e,s,d,h,u){return i(),t("div",null,e[0]||(e[0]=[o('<h1 id="ayakashibito" tabindex="-1">Ayakashibito <a class="header-anchor" href="#ayakashibito" aria-label="Permalink to &quot;Ayakashibito&quot;">​</a></h1><h2 id="installation" tabindex="-1">Installation <a class="header-anchor" href="#installation" aria-label="Permalink to &quot;Installation&quot;">​</a></h2><h3 id="windows" tabindex="-1">Windows <a class="header-anchor" href="#windows" aria-label="Permalink to &quot;Windows&quot;">​</a></h3><p>Use <a href="https://xupefei.github.io/Locale-Emulator/" target="_blank" rel="noreferrer">Locale Emulator</a>, right-click on the game and choose &quot;Run&quot; in the &quot;Locale Emulator&quot; section. Then, run the <code>aya.bat</code> included to set the needed registry key.</p><h3 id="linux-steam-deck" tabindex="-1">Linux/Steam Deck <a class="header-anchor" href="#linux-steam-deck" aria-label="Permalink to &quot;Linux/Steam Deck&quot;">​</a></h3><div class="info custom-block github-alert"><p class="custom-block-title">Requirements</p><p>Download <a href="https://www.visualnovelwiki.org/tutorials/liarsoftengine/mciqtz32.zip" target="_blank" rel="noreferrer">modified aya.bat script</a> for Linux. Tested with Lutris 7.21. The modified <code>aya.bat</code> is the exact same file except there is a new line added at the top to use chcp 932, which is SJIS encoding to correctly run the script.</p></div><h4 id="lutris" tabindex="-1">Lutris <a class="header-anchor" href="#lutris" aria-label="Permalink to &quot;Lutris&quot;">​</a></h4><ol><li><p>Add locally installed game with these settings:</p><ul><li>In &quot;Game info&quot;, select &quot;Wine&quot; for &quot;Runner&quot;</li><li>In &quot;Game options&quot;, select any prefix directory for &quot;Wine prefix&quot; and <code>aya.bat</code> for &quot;Executable&quot; (setup only)</li><li>In &quot;Runner Options&quot;, disable DXVK</li><li>In &quot;System options&quot;, set up Japanese locales with this environment variable: <code>LC_ALL=ja_JP.utf8</code></li></ul></li><li><p>Run the game.</p></li><li><p>Set the game executable to the <code>game.exe</code>.</p></li></ol><h2 id="save-data" tabindex="-1">Save data <a class="header-anchor" href="#save-data" aria-label="Permalink to &quot;Save data&quot;">​</a></h2><h3 id="windows-linux-steam-deck" tabindex="-1">Windows / Linux / Steam Deck <a class="header-anchor" href="#windows-linux-steam-deck" aria-label="Permalink to &quot;Windows / Linux / Steam Deck&quot;">​</a></h3><p>Overwrite the folder contents in <code>Ayakashibito\\save</code> (game folder).</p><h2 id="links" tabindex="-1">Links <a class="header-anchor" href="#links" aria-label="Permalink to &quot;Links&quot;">​</a></h2><ul><li><a href="https://vndb.org/v646" target="_blank" rel="noreferrer">VNDB</a></li><li><a href="https://www.steamgriddb.com/game/5356633" target="_blank" rel="noreferrer">SteamGridDB</a></li><li><a href="https://forums.fuwanovel.net/topic/4131-ayakashibito/" target="_blank" rel="noreferrer">Walkthrough</a></li></ul>',13)]))}const b=a(l,[["render",r]]);export{m as __pageData,b as default};
