import{_ as t,c as a,o,aq as i}from"./chunks/framework.CWS5zd3J.js";const p=JSON.parse(`{"title":"Deus Machina Demonbane","description":"","frontmatter":{"0":"t","1":"i","2":"t","3":"l","4":"e","5":" ","6":"=","7":" ","8":"'","9":"D","10":"e","11":"u","12":"s","13":" ","14":"M","15":"a","16":"c","17":"h","18":"i","19":"n","20":"a","21":" ","22":"D","23":"e","24":"m","25":"o","26":"n","27":"b","28":"a","29":"n","30":"e","31":"'"},"headers":[],"relativePath":"visual-novels/deus-machina-demonbane.md","filePath":"visual-novels/deus-machina-demonbane.md","lastUpdated":1747548401000}`),n={name:"visual-novels/deus-machina-demonbane.md"};function l(r,e,s,u,d,h){return o(),a("div",null,e[0]||(e[0]=[i('<h1 id="deus-machina-demonbane" tabindex="-1">Deus Machina Demonbane <a class="header-anchor" href="#deus-machina-demonbane" aria-label="Permalink to &quot;Deus Machina Demonbane&quot;">​</a></h1><h2 id="installation" tabindex="-1">Installation <a class="header-anchor" href="#installation" aria-label="Permalink to &quot;Installation&quot;">​</a></h2><h3 id="gnu-linux" tabindex="-1">GNU/Linux <a class="header-anchor" href="#gnu-linux" aria-label="Permalink to &quot;GNU/Linux&quot;">​</a></h3><h4 id="lutris" tabindex="-1">Lutris <a class="header-anchor" href="#lutris" aria-label="Permalink to &quot;Lutris&quot;">​</a></h4><div class="warning custom-block github-alert"><p class="custom-block-title">Warning</p><p>This guide is for the JAST version</p></div><p>This guide is a bit different to the other guides, as we will create a specific prefix for Demonbane.</p><ol><li><p>Add a locally installed game in Lutris</p><ul><li>In &quot;Game info&quot;, select &quot;Wine&quot; for &quot;Runner&quot;</li><li>In &quot;Game options&quot;, make a new vanilla &quot;Wine prefix&quot; folder named &quot;demonbane&quot;, select it, choose &quot;32bit&quot; for &quot;Wine architecture&quot; and &quot;Demonbane_Setup.exe&quot; for &quot;Executable&quot;</li><li>In &quot;Runner options&quot;, disable &quot;DXVK&quot;, &quot;Esync&quot;, &quot;Fsync&quot; and select &quot;Lutris 7.2&quot; for &quot;Wine version&quot;</li><li>In &quot;System options&quot;, set up Japanese locales, add the following environment variable and its value: <code>LC_ALL=ja_JP.utf8</code></li></ul></li><li><p>Install the game</p><p>Click on Play in Lutris and follow the setup. After the installation copy the folder &quot;dx&quot; from inside the Demonbane folder next to the &quot;Demonbane_Setup.exe&quot; to the folder in which you installed the game. Change the Executable under &quot;Game options&quot; to &quot;db_usa.exe&quot;.</p></li><li><p>Installing the voice patch (Optional)</p><p>Get the <a href="https://vndb.org/r76559" target="_blank" rel="noreferrer">Voice Patch from VNDB</a> and drag’n’drop the contents of &quot;Download&quot; to your game installation folder and select &quot;Overwrite&quot;.</p></li><li><p>Setup the prefix:</p><p>Click on the Wine bottle, and click &quot;Bash Terminal&quot;. Copy the following command &amp; paste it into each terminal using CTRL + SHIFT + V Hit Enter to input the command</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>winetricks -q --force lavfilters wmp10</span></span></code></pre></div><p>Close the terminal. Get the <a href="https://web.archive.org/web/20240126231520mp_/https://www.visualnovelwiki.org/tutorials/wineprefixes/quartz2.zip" target="_blank" rel="noreferrer">custom quartz files</a> The .zip contains: &quot;quartz&quot;, &quot;quartz2.dll&quot;, &quot;quartz2-win32.reg&quot;, &quot;quartz2-win64.reg&quot;.</p><div class="warning custom-block github-alert"><p class="custom-block-title">Warning</p><p>This zip also comes with &quot;quartz2-win64.reg&quot;, which can be used for 64 bit wineprefixes. &quot;quartz2.dll&quot; would also go in <code>drive_c/Windows/SysWow64</code> instead of &quot;system32&quot;. This wineprefix is using 32bit.</p></div><p>Find your demonbane folder, and navigate to <code>demonbane/drive_c/Windows/system32</code> Drag and drop the &quot;quartz2.dll&quot; file in there. Click the Wine bottle, then select &quot;Bash Terminal&quot;. Enter this command to register the &quot;quartz2.dll&quot; file in the wineprefix: <code>wine regsvr32 quartz2.dll</code></p><p>Close the terminal Click the Wine bottle again, then select &quot;Wine Registry&quot;. You’ll get a popup for the Windows registry. Click &quot;Registry&quot; in the toolbar then &quot;Import Registry File&quot;. Find &amp; import the &quot;quartz2-win32.reg&quot; file you downloaded.</p></li></ol><h2 id="save-data" tabindex="-1">Save Data <a class="header-anchor" href="#save-data" aria-label="Permalink to &quot;Save Data&quot;">​</a></h2><h3 id="windows" tabindex="-1">Windows <a class="header-anchor" href="#windows" aria-label="Permalink to &quot;Windows&quot;">​</a></h3><p>Put the contents of the ZIP file into the following folder <code>%userprofile%\\AppData/Roaming/</code></p><h3 id="linux-steam-deck" tabindex="-1">Linux / Steam Deck <a class="header-anchor" href="#linux-steam-deck" aria-label="Permalink to &quot;Linux / Steam Deck&quot;">​</a></h3><p>Put the contents of the ZIP file into the following folders inside of your prefix <code>drive_c/users/%User%/AppData/Roaming/</code></p><h2 id="links" tabindex="-1">Links <a class="header-anchor" href="#links" aria-label="Permalink to &quot;Links&quot;">​</a></h2><ul><li><a href="https://vndb.org/v231" target="_blank" rel="noreferrer">VNDB</a></li><li><a href="https://jastusa.com/games/np001" target="_blank" rel="noreferrer">JAST USA</a></li><li><a href="https://www.steamgriddb.com/game/21185" target="_blank" rel="noreferrer">SteamGridDB</a></li></ul>',14)]))}const q=t(n,[["render",l]]);export{p as __pageData,q as default};
