import{_ as t,c as a,o as i,aq as o}from"./chunks/framework.CkWLY5K4.js";const p=JSON.parse(`{"title":"Hatsukoi 1/1","description":"","frontmatter":{"0":"t","1":"i","2":"t","3":"l","4":"e","5":" ","6":"=","7":" ","8":"'","9":"H","10":"a","11":"t","12":"s","13":"u","14":"k","15":"o","16":"i","17":" ","18":"1","19":"/","20":"1","21":"'"},"headers":[],"relativePath":"visual-novels/hatsukoi-1-1.md","filePath":"visual-novels/hatsukoi-1-1.md","lastUpdated":1747549240000}`),l={name:"visual-novels/hatsukoi-1-1.md"};function s(n,e,r,u,h,d){return i(),a("div",null,e[0]||(e[0]=[o('<h1 id="hatsukoi-1-1" tabindex="-1">Hatsukoi 1/1 <a class="header-anchor" href="#hatsukoi-1-1" aria-label="Permalink to &quot;Hatsukoi 1/1&quot;">​</a></h1><h2 id="installation" tabindex="-1">Installation <a class="header-anchor" href="#installation" aria-label="Permalink to &quot;Installation&quot;">​</a></h2><h3 id="linux" tabindex="-1">Linux <a class="header-anchor" href="#linux" aria-label="Permalink to &quot;Linux&quot;">​</a></h3><div class="info custom-block github-alert"><p class="custom-block-title">Information</p><p>Tested with <a href="/visual-novel-wiki/linux/adding-wine-versions.html">Lutris-GE-Proton 7-32</a>.</p></div><div class="warning custom-block github-alert"><p class="custom-block-title">Warning</p><p>We will be using the disk installer version (it has two disk images), mount it using <a href="/visual-novel-wiki/linux/cdemu.html">CDEmu</a>. You will need to mount the disk image everytime you want to play.</p></div><p>Download <a href="https://tsurezurescans.wordpress.com/2018/09/27/hatsukoi-1-1-full-english-patch-main-game-append-scenarios/" target="_blank" rel="noreferrer">the english patch from Tsurezure Scans</a>.</p><h4 id="lutris" tabindex="-1">Lutris <a class="header-anchor" href="#lutris" aria-label="Permalink to &quot;Lutris&quot;">​</a></h4><ol><li><p>Add locally installed game with these settings:</p><ul><li>In &quot;Game info&quot;, select &quot;Wine&quot; for &quot;Runner&quot;</li><li>In &quot;Game options&quot;, select any prefix directory for &quot;Wine prefix&quot; and <code>Autorun.exe</code> for &quot;Executable&quot; (installation only)</li><li>In &quot;System options&quot;, set up Japanese locales with this environment variable: <code>LC_ALL=ja_JP.utf8</code></li></ul></li><li><p>Run <code>Autorun.exe</code> and install the game using the first button</p></li><li><p>Click on the button with &quot;Y&quot;.</p></li><li><p>The installer will wait for the second disk. Instead, stop the installer using Lutris.</p></li><li><p>Locate the <code>setupdata/gamedata</code> directory inside the second disk, copy all the content (not the directory) inside the installation folder in your Wine prefix.</p></li><li><p>Replace all the content of the patch inside the same directory.</p></li><li><p>Edit the configuration of the game:</p><ul><li>In &quot;Game options&quot;, select <code>Launcher.exe</code> for &quot;Executable&quot;</li></ul></li><li><p>Run the game for the first time, leave the first disk mounted and press &quot;OK&quot;. If everything works, you should get a message and a confirmation from the Tsurezure Scans&#39; patch.</p></li></ol><h2 id="save-data" tabindex="-1">Save data <a class="header-anchor" href="#save-data" aria-label="Permalink to &quot;Save data&quot;">​</a></h2><h3 id="windows" tabindex="-1">Windows <a class="header-anchor" href="#windows" aria-label="Permalink to &quot;Windows&quot;">​</a></h3><p>Put the &quot;global.sav&quot; file into <code>C:\\Program Files (x86)\\toneworks\\Hatsukoi\\savedata\\</code> directory.</p><h2 id="links" tabindex="-1">Links <a class="header-anchor" href="#links" aria-label="Permalink to &quot;Links&quot;">​</a></h2><ul><li><a href="https://vndb.org/v9124" target="_blank" rel="noreferrer">VNDB</a></li><li><a href="https://www.steamgriddb.com/game/5353498" target="_blank" rel="noreferrer">SteamGridDB</a></li><li><a href="https://forums.fuwanovel.net/topic/17689-hatsukoi-11/" target="_blank" rel="noreferrer">Walkthrough</a></li><li><a href="https://tsurezurescans.wordpress.com/2018/09/27/hatsukoi-1-1-full-english-patch-main-game-append-scenarios/" target="_blank" rel="noreferrer">Tsurezure Scans&#39; english patch</a></li></ul>',13)]))}const m=t(l,[["render",s]]);export{p as __pageData,m as default};
