import{_ as a,c as t,o as n,aq as o}from"./chunks/framework.CWS5zd3J.js";const p=JSON.parse(`{"title":"Never7 -the end of infinity-","description":"","frontmatter":{"0":"t","1":"i","2":"t","3":"l","4":"e","5":" ","6":"=","7":" ","8":"'","9":"N","10":"e","11":"v","12":"e","13":"r","14":"7","15":" ","16":"-","17":"t","18":"h","19":"e","20":" ","21":"e","22":"n","23":"d","24":" ","25":"o","26":"f","27":" ","28":"i","29":"n","30":"f","31":"i","32":"n","33":"i","34":"t","35":"y","36":"-","37":"'"},"headers":[],"relativePath":"visual-novels/never7.md","filePath":"visual-novels/never7.md","lastUpdated":1747548401000}`),r={name:"visual-novels/never7.md"};function i(l,e,s,d,h,u){return n(),t("div",null,e[0]||(e[0]=[o('<h1 id="never7-the-end-of-infinity" tabindex="-1">Never7 -the end of infinity- <a class="header-anchor" href="#never7-the-end-of-infinity" aria-label="Permalink to &quot;Never7 -the end of infinity-&quot;">​</a></h1><h2 id="installation" tabindex="-1">Installation <a class="header-anchor" href="#installation" aria-label="Permalink to &quot;Installation&quot;">​</a></h2><h3 id="windows" tabindex="-1">Windows <a class="header-anchor" href="#windows" aria-label="Permalink to &quot;Windows&quot;">​</a></h3><p>First, we’ll need to get the <a href="https://www.love2d.org/" target="_blank" rel="noreferrer">Love2D engine</a>. Make sure you have the right 64bit/32bit version depending on your system. Thenn, install Love2D engine Installer (non-zip), open the n7e.love game file and enjoy!</p><div class="info custom-block github-alert"><p class="custom-block-title">Info</p><p>If you chose the zip installer, you’ll have to drag and drop n7e.love into the love file inside of the zip to run-- in other words, this is the same as: <code>love n7e.love in the terminal</code></p></div><h3 id="gnu-linux" tabindex="-1">GNU/Linux <a class="header-anchor" href="#gnu-linux" aria-label="Permalink to &quot;GNU/Linux&quot;">​</a></h3><p>Download the <a href="https://www.love2d.org/" target="_blank" rel="noreferrer">Love2D engine AppImage</a> release. This will allow you to play the game natively. Run &quot;n7e.love&quot; using the Love2D AppImage in the terminal <code>&lt;path-to-love2d&gt; &lt;path-to-n7e&gt;</code>. To run using Steam, add the AppImage as a non-Steam game. Enter the Properties, then add <code>&lt;path-to-n7e&gt;</code> after the AppImage path. Finally, run the game an enjoy!</p><h3 id="steam-deck" tabindex="-1">Steam Deck <a class="header-anchor" href="#steam-deck" aria-label="Permalink to &quot;Steam Deck&quot;">​</a></h3><p>Same as Linux, use &quot;Desktop Mode&quot; to add and use &quot;Mouse Only&quot; control template in game mode.</p><h2 id="save-data" tabindex="-1">Save Data <a class="header-anchor" href="#save-data" aria-label="Permalink to &quot;Save Data&quot;">​</a></h2><h3 id="windows-1" tabindex="-1">Windows <a class="header-anchor" href="#windows-1" aria-label="Permalink to &quot;Windows&quot;">​</a></h3><p>Overwrite the folder contents in <code>C:\\Users\\user\\AppData\\Roaming\\LOVE\\Never7Eternal</code>.</p><h3 id="linux-steam-deck" tabindex="-1">Linux/Steam Deck <a class="header-anchor" href="#linux-steam-deck" aria-label="Permalink to &quot;Linux/Steam Deck&quot;">​</a></h3><p>Overwrite the folder contents in <code>~/.local/share/love/Never7Eternal</code>.</p><h2 id="links" tabindex="-1">Links <a class="header-anchor" href="#links" aria-label="Permalink to &quot;Links&quot;">​</a></h2><ul><li><a href="https://vndb.org/v248" target="_blank" rel="noreferrer">VNDB</a></li><li><a href="https://www.steamgriddb.com/game/34700" target="_blank" rel="noreferrer">SteamGridDB</a></li><li><a href="https://forums.fuwanovel.net/topic/731-never-7/" target="_blank" rel="noreferrer">Walkthrough</a></li></ul>',16)]))}const m=a(r,[["render",i]]);export{p as __pageData,m as default};
