import{_ as a,c as t,o as i,aq as o}from"./chunks/framework.CWS5zd3J.js";const h=JSON.parse(`{"title":"Sakura no Toki","description":"","frontmatter":{"0":"t","1":"i","2":"t","3":"l","4":"e","5":" ","6":"=","7":" ","8":"'","9":"S","10":"a","11":"k","12":"u","13":"r","14":"a","15":" ","16":"n","17":"o","18":" ","19":"T","20":"o","21":"k","22":"i","23":"'"},"headers":[],"relativePath":"visual-novels/sakura-no-toki.md","filePath":"visual-novels/sakura-no-toki.md","lastUpdated":1747548401000}`),n={name:"visual-novels/sakura-no-toki.md"};function l(s,e,r,d,u,c){return i(),t("div",null,e[0]||(e[0]=[o(`<h1 id="sakura-no-toki" tabindex="-1">Sakura no Toki <a class="header-anchor" href="#sakura-no-toki" aria-label="Permalink to &quot;Sakura no Toki&quot;">​</a></h1><h2 id="installation" tabindex="-1">Installation <a class="header-anchor" href="#installation" aria-label="Permalink to &quot;Installation&quot;">​</a></h2><h3 id="linux" tabindex="-1">Linux <a class="header-anchor" href="#linux" aria-label="Permalink to &quot;Linux&quot;">​</a></h3><div class="info custom-block github-alert"><p class="custom-block-title">Requirements</p><p>Download <a href="https://github.com/z0z0z/mf-install/archive/refs/heads/master.zip" target="_blank" rel="noreferrer">mf-install</a> and <a href="https://www.visualnovelwiki.org/tutorials/v20431/w7-x86.zip" target="_blank" rel="noreferrer">these libraries</a> (contains quartz2.dll, evr.dll, dxva2.dll). Tested with Lutris 7.2, new prefix needed.</p></div><h4 id="lutris" tabindex="-1">Lutris <a class="header-anchor" href="#lutris" aria-label="Permalink to &quot;Lutris&quot;">​</a></h4><ol><li><p>Add locally installed game with these settings:</p><ul><li>In &quot;Game info&quot;, select &quot;Wine&quot; for &quot;Runner&quot;</li><li>In &quot;Game options&quot;, create and/or select &quot;artemisengine&quot; prefix directory for &quot;Wine prefix&quot; and <code>sakuranotoki.exe</code> for &quot;Executable&quot;</li><li>In &quot;System options&quot;, set up Japanese locales with this environment variable: <code>LC_ALL=ja_JP.utf8</code></li><li>In &quot;Runner Options&quot;, disable DXVK, Esync and Fsync</li></ul></li><li><p>Disable &quot;winegstreamer&quot; in &quot;winecfg&quot;.</p></li><li><p>Uncompress the <a href="https://github.com/z0z0z/mf-install/archive/refs/heads/master.zip" target="_blank" rel="noreferrer">mf-install</a> archive downloaded.</p></li><li><p>Go into the folder and right click <code>mf-install.sh</code> and click on &quot;Copy Location&quot;.</p></li><li><p>In Lutris, select &quot;artemisengine&quot;, click the Wine bottle at the bottom and click &quot;Bash Terminal&quot;.</p></li><li><p>Paste your clipboard in the terminal.</p></li><li><p>In the Bash terminal, run the following command to install &quot;LAVFilters&quot; <code>winetricks -q lavfilters</code>.</p></li><li><p>Uncompress <code>w7-x86.zip</code> downloaded.</p></li><li><p>Copy the three files inside (<code>quartz2.dll</code>, <code>evr.dll</code>, <code>dxva2.dll</code>).</p></li><li><p>Paste the three files into <code>drive_c/windows/syswow64</code>, inside &quot;artemisengine&quot; prefix directory</p></li><li><p>Register DLLs by running the following commands in the Bash terminal:</p></li></ol><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>wine regsvr32 quartz2.dll</span></span>
<span class="line"><span>wine regsvr32 evr.dll</span></span>
<span class="line"><span>wine reg add &quot;HKEY_CLASSES_ROOT\\\\CLSID\\\\{79376820-07D0-11CF-A24D-0020AFD79767}\\\\InprocServer32&quot; /f /t REG_SZ /d c:\\\\windows\\\\system32\\\\quartz.dll</span></span>
<span class="line"><span>wine reg add &quot;HKEY_CURRENT_USER\\\\Software\\\\LAV\\\\Audio\\\\Formats&quot; /f /t REG_DWORD /v wmalossless /d 1</span></span>
<span class="line"><span>wine reg add &quot;HKCU\\\\Software\\\\LAV\\\\Video\\\\Output&quot; /f /t REG_DWORD /v yuy2 /d 0</span></span></code></pre></div><div class="warning custom-block github-alert"><p class="custom-block-title">Warning</p><p>Running the game with Gamescope is recommended to avoid video flickering and bugged always on top fullscreen the game has by default.</p></div><h4 id="steam" tabindex="-1">Steam <a class="header-anchor" href="#steam" aria-label="Permalink to &quot;Steam&quot;">​</a></h4><p>Right-click on the Lutris entry and select &quot;Create Steam shortcut&quot;.</p><h3 id="steam-deck" tabindex="-1">Steam Deck <a class="header-anchor" href="#steam-deck" aria-label="Permalink to &quot;Steam Deck&quot;">​</a></h3><p>Same as Linux.</p><div class="warning custom-block github-alert"><p class="custom-block-title">Warning</p><p>Run in game mode to avoid video flickering.</p></div><h2 id="save-data" tabindex="-1">Save Data <a class="header-anchor" href="#save-data" aria-label="Permalink to &quot;Save Data&quot;">​</a></h2><h3 id="windows" tabindex="-1">Windows <a class="header-anchor" href="#windows" aria-label="Permalink to &quot;Windows&quot;">​</a></h3><p>Overwrite the folder contents in <code>C:\\users\\&lt;username&gt;\\AppData\\Roaming\\枕\\サクラノ刻</code>.</p><h2 id="links" tabindex="-1">Links <a class="header-anchor" href="#links" aria-label="Permalink to &quot;Links&quot;">​</a></h2><ul><li><a href="https://vndb.org/v20431" target="_blank" rel="noreferrer">VNDB</a></li></ul>`,18)]))}const m=a(n,[["render",l]]);export{h as __pageData,m as default};
