import{_ as t,c as a,o,aq as i}from"./chunks/framework.CkWLY5K4.js";const m=JSON.parse(`{"title":"Steins;Gate","description":"","frontmatter":{"0":"t","1":"i","2":"t","3":"l","4":"e","5":" ","6":"=","7":" ","8":"'","9":"S","10":"t","11":"e","12":"i","13":"n","14":"s","15":";","16":"G","17":"a","18":"t","19":"e","20":"'"},"headers":[],"relativePath":"visual-novels/steins-gate.md","filePath":"visual-novels/steins-gate.md","lastUpdated":1747549240000}`),r={name:"visual-novels/steins-gate.md"};function s(l,e,n,d,c,h){return o(),a("div",null,e[0]||(e[0]=[i('<h1 id="steins-gate" tabindex="-1">Steins;Gate <a class="header-anchor" href="#steins-gate" aria-label="Permalink to &quot;Steins;Gate&quot;">​</a></h1><h2 id="installation" tabindex="-1">Installation <a class="header-anchor" href="#installation" aria-label="Permalink to &quot;Installation&quot;">​</a></h2><h3 id="committee-of-zero-patch" tabindex="-1">Committee of Zero patch <a class="header-anchor" href="#committee-of-zero-patch" aria-label="Permalink to &quot;Committee of Zero patch&quot;">​</a></h3><p><a href="http://sonome.dareno.me/projects/sghd.html" target="_blank" rel="noreferrer">Committee of Zero website</a></p><h4 id="windows" tabindex="-1">Windows <a class="header-anchor" href="#windows" aria-label="Permalink to &quot;Windows&quot;">​</a></h4><ol><li>Download the installer. You want <code>SGPatch-v&lt;version&gt;-Setup.zip</code>, not the source code.</li><li>Extract the archive somewhere on your hard drive, not in your game directory.</li><li>Go to the newly created <code>SGPatch-v&lt;version&gt;-Setup</code> folder and run &quot;SGPatch-Installer.exe&quot; (be careful, the installer has sound)</li></ol><div class="warning custom-block github-alert"><p class="custom-block-title">Warning</p><p>If the installer quits with an error about &quot;MSVCP140_1.dll&quot;, install &quot;Visual C++ Redistributable&quot; (32-bit version, &quot;vc_redist.x86.exe&quot;, regardless of your system) and try again.</p></div><ol start="4"><li>After you click &quot;Finish&quot; and the installer closes, you can delete the <code>SGPatch-v&lt;version&gt;-Setup</code> folder.</li></ol><h4 id="linux-steam-deck" tabindex="-1">Linux / Steam Deck <a class="header-anchor" href="#linux-steam-deck" aria-label="Permalink to &quot;Linux / Steam Deck&quot;">​</a></h4><ol><li>Download the installer. You want <code>SGPatch-v&lt;version&gt;-Setup.zip</code>, not the source code.</li><li>Extract the archive somewhere on your hard drive, not in your game directory. You’ll get a new folder called <code>SGPatch-v&lt;version&gt;-Setup</code>. Memorise this location.</li><li>Go to Steam, and add a non-Steam game. Navigate to the <code>SGPatch-v&lt;version&gt;-Setup</code> folder and add &quot;SGPatch-Installer.exe&quot; as a game.</li><li>Right-click &quot;SGPatch-Installer.exe&quot;, into &quot;Properties&quot; and &quot;Launch Options&quot;, and run the following command depending on where you installed Steins;Gate:</li></ol><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>STEAM_COMPAT_DATA_PATH=&quot;/home/deck/.local/share/Steam/steamapps/compatdata/412830&quot; %command%</span></span></code></pre></div><div class="warning custom-block github-alert"><p class="custom-block-title">Warning</p><p>You need to change the path between quotes of the command depending on your system. For Steam Deck, you can just copy this command as-is (if installed on Internal Storage)</p></div><div class="info custom-block github-alert"><p class="custom-block-title">Information</p><p>Normally, this compatdata folder is found in your &quot;.steam&quot; folder; <code>~/.steam/steam/steamapps/compatdata/412830</code> where &quot;412830&quot; is the app ID of Steins;Gate on Steam. This path between quotes is Steins;Gate’s compatdata folder – it’ll have a &quot;drive_c&quot; folder inside of it, to make sure you have the right one.</p></div><ol start="5"><li>Run the installer (be careful, it has sound).</li></ol><h2 id="save-data" tabindex="-1">Save Data <a class="header-anchor" href="#save-data" aria-label="Permalink to &quot;Save Data&quot;">​</a></h2><h3 id="windows-1" tabindex="-1">Windows <a class="header-anchor" href="#windows-1" aria-label="Permalink to &quot;Windows&quot;">​</a></h3><p>Overwrite &quot;SAVEDATA.dat&quot; in <code>Documents\\My Games\\mages_steam\\STEINS;GATE\\eng</code></p><h2 id="links" tabindex="-1">Links <a class="header-anchor" href="#links" aria-label="Permalink to &quot;Links&quot;">​</a></h2><ul><li><a href="https://vndb.org/v2002" target="_blank" rel="noreferrer">VNDB</a></li><li><a href="https://www.steamgriddb.com/game/9557" target="_blank" rel="noreferrer">SteamGridDB</a></li><li><a href="https://store.steampowered.com/app/412830/STEINSGATE/" target="_blank" rel="noreferrer">Steam</a> (newer version with HD artwork, compatible with Committee of Zero improvement patch)</li><li><a href="https://jastusa.com/games/sg001/steinsgate" target="_blank" rel="noreferrer">JAST USA</a> (older DRM-free version)</li><li><a href="https://steamcommunity.com/sharedfiles/filedetails/?id=2600680431" target="_blank" rel="noreferrer">Walkthrough</a></li></ul>',19)]))}const p=t(r,[["render",s]]);export{m as __pageData,p as default};
